// src/components/MovieCard.js
import React from 'react';
import './MovieCard.css';

const MovieCard = ({ title, poster_url, genres, reason }) => {
  return (
    <div className="movie-card">
      <img
        src={poster_url || "https://via.assets.so/img.jpg?w=500&h=750&text=No+Image"}
        alt={title}
        onError={(e) => {
          e.target.onerror = null;
          e.target.src = "https://via.assets.so/img.jpg?w=500&h=750&text=No+Image";
        }}
      />
      <div className="overlay">
        <h3>{title}</h3>
        <p>{genres?.join(', ')}</p>
        <p className="reason">{reason}</p>
      </div>
    </div>
  );
};

export default MovieCard;
