import React, { useEffect, useState } from 'react';
import { getWatchlist } from '../services/api';
import MovieCard from '../components/MovieCard';

const WatchlistPage = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const userId = 'test_user';

  useEffect(() => {
    getWatchlist(userId)
      .then(res => {
        console.log("WATCHLIST_BACKEND:", res.data);
        const watchlist = res.data?.watchlist;
        if (Array.isArray(watchlist)) {
          setMovies(watchlist);
        } else {
          setMovies([]);
          console.warn("watchlist nu e array");
        }
      })
      .catch(err => {
        console.error("Eroare la getWatchlist:", err);
        setError("Nu s-a putut încărca watchlist-ul.");
        setMovies([]);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Se încarcă watchlist-ul...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h2>Watchlist</h2>
      {movies.length === 0 ? (
        <p>Nu ai filme în watchlist.</p>
      ) : (
        <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
          {movies.map((movie, index) => (
            <MovieCard key={index} {...movie} />
          ))}
        </div>
      )}
    </div>
  );
};

export default WatchlistPage;
