// src/services/api.js
import axios from 'axios';

const BASE_URL = 'http://localhost:8080'; // Înlocuit după deploy

export const getWatchlist = (userId) =>
  axios.get(`${BASE_URL}/?user_id=${userId}`);

export const generateRecommendations = (userId) =>
  axios.get(`${BASE_URL}/generate_recommendations?user_id=${userId}`);

export const getRecommendations = (userId) =>
  axios.get(`${BASE_URL}/get_recommendations?user_id=${userId}`);
