import React, { useEffect, useState } from 'react';
import { getWatchlist } from '../services/api';
import MovieCard from '../components/MovieCard';

const WatchlistPage = () => {
  const [watchlist, setWatchlist] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const userId = 'test_user';

  useEffect(() => {
    getWatchlist(userId)
      .then(res => {
        console.log("WATCHLIST_BACKEND:", res.data);
        const wl = res.data?.watchlist;

        if (Array.isArray(wl)) {
          setWatchlist(wl);
        } else {
          console.warn("⚠️ watchlist nu e array", typeof wl);
          setWatchlist([]);
        }
      })
      .catch(err => {
        console.error("Eroare la fetch watchlist:", err);
        setError("A apărut o eroare la încărcarea watchlist-ului.");
        setWatchlist([]);
      })
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <p>Se încarcă filmele...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h2>Watchlist</h2>
      {watchlist.length === 0 ? (
        <p>Nu ai filme în watchlist.</p>
      ) : (
        <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
          {watchlist.map((movie, index) => (
            <MovieCard key={index} {...movie} />
          ))}
        </div>
      )}
    </div>
  );
};

export default WatchlistPage;
