import React, { useState } from 'react';
import { addToWatchlist } from '../services/api';

const AddMovieForm = () => {
  const [form, setForm] = useState({
    id: '',
    title: '',
    overview: '',
    poster_url: '',
    genres: '',
  });

  const [success, setSuccess] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const genresArray = form.genres.split(',').map((g) => g.trim());

    const movieData = {
      id: form.id,
      title: form.title,
      overview: form.overview,
      poster_url: form.poster_url,
      genres: genresArray,
    };

    try {
      await addToWatchlist('test_user', movieData);
      setSuccess(true);
      setForm({
        id: '',
        title: '',
        overview: '',
        poster_url: '',
        genres: '',
      });
    } catch (err) {
      console.error(err);
      setSuccess(false);
    }
  };

  return (
    <div style={{ padding: '2rem', maxWidth: '600px', margin: '0 auto' }}>
      <h2>Adaugă un film în Watchlist</h2>
      <form onSubmit={handleSubmit}>
        <input name="id" placeholder="ID Film" value={form.id} onChange={handleChange} required /><br />
        <input name="title" placeholder="Titlu" value={form.title} onChange={handleChange} required /><br />
        <input name="poster_url" placeholder="Poster URL" value={form.poster_url} onChange={handleChange} /><br />
        <input name="genres" placeholder="Genuri (ex: Drama, Thriller)" value={form.genres} onChange={handleChange} required /><br />
        <textarea name="overview" placeholder="Descriere" value={form.overview} onChange={handleChange} /><br />
        <button type="submit">Adaugă Film</button>
      </form>
      {success && <p style={{ color: 'green' }}>✅ Film adăugat în watchlist!</p>}
    </div>
  );
};

export default AddMovieForm;
