import React, { useEffect, useState } from 'react';
import { getRecommendations } from '../services/api';
import MovieCard from '../components/MovieCard';

const RecommendationsPage = () => {
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const userId = 'test_user';

  useEffect(() => {
  getRecommendations(userId)
    .then(res => {
      console.log("REZ_BACKEND:", res.data);
      const recs = res.data?.recommendations;  // <- în loc de recommendations

      if (Array.isArray(recs)) {
        setRecommendations(recs);
      } else {
        setRecommendations([]);
        console.warn("Expected array, got:", typeof recs);
      }
    })
    .catch(err => {
      console.error("Error fetching data:", err);
      setRecommendations([]);
    });
}, []);


  if (loading) return <p>Se încarcă recomandările...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h2>Recomandări</h2>
      {recommendations.length === 0 ? (
        <p>Nu există recomandări momentan.</p>
      ) : (
        <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
          {recommendations.map((movie, index) => (
            <MovieCard key={index} {...movie} />
          ))}
        </div>
      )}
    </div>
  );
};

export default RecommendationsPage;
